    star_test!(example2a, star1, "()()", "0");
    star_test!(example3, star1, "(((", "3");
    star_test!(example4, star1, "(()(()(", "3");
    star_test!(example5, star1, "))(((((", "3");
    star_test!(example6, star1, "())", "-1");
    star_test!(example7, star1, "())", "-1");
    star_test!(example8, star1, ")))", "-3");
    star_test!(example9, star1, ")())())", "-3");
    star_test!(me1, star1, ME, "74");

